import React, { Component } from 'react';
import {
	Platform,
	StyleSheet,
	Text,
	View,
	Animated,
	PanResponder,
	Dimensions,
	ScrollView,
	Image,
	Slider,
	StatusBar
} from 'react-native';

import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

const { height: SCREEN_HEIGHT, width: SCREEN_WIDTH } = Dimensions.get('window');

type Props = {};

export default class App extends Component<Props> {
	state = {
		isScrollEnabled: false
	};

	componentWillMount() {
		this.scrollOffset = 0;
		this.animation = new Animated.ValueXY({ x: 0, y: SCREEN_HEIGHT - 80 });
		this.panResponder = PanResponder.create({
			onMoveShouldSetPanResponder: (evt, gestureState) => {
				if (
					(this.state.isScrollEnabled &&
						this.scrollOffset <= 0 &&
						gestureState.dy > 0) ||
					(!this.state.isScrollEnabled && gestureState.dy < 0)
				) {
					return true;
				}
			},
			onPanResponderGrant: (evt, gestureState) => {
				this.animation.extractOffset();
			},
			onPanResponderMove: (evt, gestureState) => {
				this.animation.setValue({ x: 0, y: gestureState.dy });
			},
			onPanResponderRelease: (evt, gestureState) => {
				if (gestureState.moveY > SCREEN_HEIGHT - 105) {
					Animated.spring(this.animation.y, {
						toValue: 0,
						tension: 1
					}).start();
				} else if (gestureState.moveY < 105) {
					Animated.spring(this.animation.y, {
						toValue: 0,
						tension: 1
					}).start();
				} else if (gestureState.dy < 0) {
					this.setState({ isScrollEnabled: true });
					Animated.spring(this.animation.y, {
						toValue: -SCREEN_HEIGHT + 105,
						tension: 1
					}).start();
				} else if (gestureState.dy > 0) {
					this.minimize(() => {});
				}
			}
		});
	}
	minimize(cb) {
		this.setState({ isScrollEnabled: false });
		cb && cb();
		Animated.spring(this.animation.y, {
			toValue: SCREEN_HEIGHT - 105,
			tension: 1
		}).start();
	}

	render() {
		const animatedHeight = {
			transform: this.animation.getTranslateTransform()
		};
		const animatedImageHeight = this.animation.y.interpolate({
			inputRange: [0, SCREEN_HEIGHT - 80],
			outputRange: [200, 32],
			extrapolate: 'clamp'
		});
		const animatedSongOpacity = this.animation.y.interpolate({
			inputRange: [0, SCREEN_HEIGHT - 500, SCREEN_HEIGHT - 80],
			outputRange: [0, 0, 1],
			extrapolate: 'clamp'
		});
		const animatedImageMarginLeft = this.animation.y.interpolate({
			inputRange: [0, SCREEN_HEIGHT - 80],
			outputRange: [SCREEN_WIDTH / 2 - 105, 10],
			extrapolate: 'clamp'
		});
		const animatedHeaderHeight = this.animation.y.interpolate({
			inputRange: [0, SCREEN_HEIGHT - 80],
			outputRange: [SCREEN_HEIGHT / 2, 80],
			extrapolate: 'clamp'
		});
		const animatedSongDetailOpacity = this.animation.y.interpolate({
			inputRange: [0, SCREEN_HEIGHT - 500, SCREEN_HEIGHT - 80],
			outputRange: [1, 0, 0],
			extrapolate: 'clamp'
		});
		const animatedBackgroundColor = this.animation.y.interpolate({
			inputRange: [0, SCREEN_HEIGHT - 80],
			outputRange: ['rgba(0,0,0,0.5)', 'white'],
			extrapolate: 'clamp'
		});
		return (
			<Animated.View
				style={{ flex: 1, backgroundColor: animatedBackgroundColor }}
			>
				<StatusBar
					translucent
					backgroundColor={'rgba(0,0,0,0)'}
					barStyle="dark-content"
				/>
				<Animated.View
					{...this.panResponder.panHandlers}
					style={[
						animatedHeight,
						{
							position: 'absolute',
							left: 0,
							right: 0,
							zIndex: 10,
							backgroundColor: 'white',
							height: SCREEN_HEIGHT
						}
					]}
				>
					<ScrollView
						scrollEnabled={this.state.isScrollEnabled}
						// scrollEventThrottle={16}
						onScroll={event => {
							this.scrollOffset = event.nativeEvent.contentOffset.y;
						}}
					>
						<Animated.View
							style={{
								height: animatedHeaderHeight,
								borderTopWidth: 1,
								borderTopColor: '#ebe5e5',
								flexDirection: 'row',
								alignItems: 'center'
							}}
						>
							<Animated.View
								style={{
									opacity: animatedSongDetailOpacity,
									position: 'absolute',
									top: 10,
									left: 10
								}}
							>
								<Icon
									name="chevron-down"
									size={32}
									color="black"
									onPress={() =>
										this.minimize(() => this.animation.extractOffset())
									}
								/>
							</Animated.View>
							<View
								style={{
									flex: 4,
									flexDirection: 'row',
									alignItems: 'center'
								}}
							>
								<Animated.View
									style={{
										height: animatedImageHeight,
										width: animatedImageHeight,
										marginLeft: animatedImageMarginLeft
									}}
								>
									<Image
										style={{ flex: 1, height: null, width: null }}
										source={{
											uri:
												'data:image/jpeg;base64,'
										}}
									/>
								</Animated.View>
								<Animated.Text
									style={{
										opacity: animatedSongOpacity,
										fontSize: 18,
										paddingLeft: 10,
										color: 'black'
									}}
								>
									Nightmare
								</Animated.Text>
							</View>
							<Animated.View
								style={{
									opacity: animatedSongOpacity,
									flex: 1,
									flexDirection: 'row',
									justifyContent: 'space-around'
								}}
							>
								<Icon name="pause" size={32} color="black" />
								<Icon name="play" size={32} color="black" />
							</Animated.View>
						</Animated.View>
						<Animated.View
							style={{
								height: animatedHeaderHeight,
								opacity: animatedSongDetailOpacity
							}}
						>
							<View
								style={{
									flex: 1,
									alignItems: 'center',
									justifyContent: 'flex-end'
								}}
							>
								<Text
									style={{ fontSize: 22, fontWeight: 'bold', color: 'black' }}
								>
									Nightmare
								</Text>
								<Text
									style={{ fontSize: 18, fontWeight: 'bold', color: '#fa95ed' }}
								>
									Polyphia
								</Text>
							</View>
							<View
								style={{
									height: 40,
									width: SCREEN_WIDTH,
									alignItems: 'center'
								}}
							>
								<Slider
									style={{ width: 300 }}
									step={1}
									minimumValue={18}
									maximumValue={71}
									value={18}
									thumbTintColor="black"
								/>
							</View>
							<View
								style={{
									flex: 1,
									flexDirection: 'row',
									alignItems: 'center',
									justifyContent: 'space-around'
								}}
							>
								<Icon name="rewind" size={40} color="black" />
								<Icon name="pause" size={50} color="black" />
								<Icon name="fast-forward" size={40} color="black" />
							</View>
							<View
								style={{
									flexDirection: 'row',
									justifyContent: 'space-between',
									paddingHorizontal: 20,
									paddingBottom: 20
								}}
							>
								<Icon name="plus" size={32} color="black" />
								<Icon name="dots-vertical" size={32} color="black" />
							</View>
						</Animated.View>
						<View style={{ height: 1050 }} />
					</ScrollView>
				</Animated.View>
			</Animated.View>
		);
	}
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		justifyContent: 'center',
		alignItems: 'center',
		backgroundColor: '#FFFFFF'
	},
	welcome: {
		fontSize: 20,
		textAlign: 'center',
		margin: 10
	},
	instructions: {
		textAlign: 'center',
		color: '#333333',
		marginBottom: 5
	}
});
